
$(function () {
  // Admin Panel settings

  //****************************
  /* This is for the mini-sidebar if width is less then 1170*/
  //****************************
  var setsidebartype = function () {
    var width =
      window.innerWidth > 0 ? window.innerWidth : this.screen.width;
    if (width < 1199) {
      $("#main-wrapper").attr("data-sidebartype", "mini-sidebar");
      $("#main-wrapper").addClass("mini-sidebar");
    } else {
      $("#main-wrapper").attr("data-sidebartype", "full");
      $("#main-wrapper").removeClass("mini-sidebar");
    }
  };
  $(window).ready(setsidebartype);
  $(window).on("resize", setsidebartype);
  //****************************
  /* This is for sidebartoggler*/
  //****************************
  $(".sidebartoggler").on("click", function () {
    $("#main-wrapper").toggleClass("mini-sidebar");
    if ($("#main-wrapper").hasClass("mini-sidebar")) {
      $(".sidebartoggler").prop("checked", !0);
      $("#main-wrapper").attr("data-sidebartype", "mini-sidebar");
    } else {
      $(".sidebartoggler").prop("checked", !1);
      $("#main-wrapper").attr("data-sidebartype", "full");
    }
  });
  $(".sidebartoggler").on("click", function () {
    $("#main-wrapper").toggleClass("show-sidebar");
  });
})


document.addEventListener('DOMContentLoaded', function () {
  const addTaskBtn = document.getElementById('addTaskBtn');
  const loadMoreTasksBtn = document.getElementById('loadMoreTasksBtn');
  const tasksContainer = document.getElementById('tasksContainer');
  let taskCount = 1; // Initial task count
  const maxTasks = 100; // Maximum number of tasks

  function addTask() {
      if (taskCount < maxTasks) {
          const taskItem = document.createElement('div');
          taskItem.classList.add('task-item', 'mb-3');
          taskItem.innerHTML = `
              <input type="text" class="form-control mb-2" placeholder="Task Name" required>
              <input type="number" class="form-control" placeholder="Expected Hours" required>
          `;
          tasksContainer.appendChild(taskItem);
          taskCount++;
      }
  }

  function loadMoreTasks() {
      // Load more tasks (e.g., 20 at a time)
      for (let i = 0; i < 20; i++) {
          addTask();
      }
      if (taskCount >= maxTasks) {
          loadMoreTasksBtn.style.display = 'none'; // Hide button when max tasks reached
      }
  }

  addTaskBtn.addEventListener('click', addTask);
  loadMoreTasksBtn.addEventListener('click', loadMoreTasks);
});
